import React, {Component} from 'react';
import { Stylesheet, Text, View, TextInput, Button} from 'react-native';
import  firebase from 'firebase/app';
import 'firebase/database'
import 'firebase/'
import 'firebase/auth';
import 'firebase/storage';



  
//firebase.initializeApp(firebaseConfig);
  
export default class Sensor extends Component{

    constructor(props) {
        super(props);
        this.state = {
            Portao: '',
            valor: '',
            };
          
        let firebaseConfig = {
            apiKey: "AIzaSyA8LQOWe1hwNZzP3yxfYepZoHf4M-ds8D8",
            authDomain: "holdor-661d2.firebaseapp.com",
            databaseURL: "https://holdor-661d2-default-rtdb.firebaseio.com",
            projectId: "holdor-661d2",
            storageBucket: "holdor-661d2.appspot.com",
            messagingSenderId: "959425559187",
            appId: "1:959425559187:web:3d6444684619296a9b6b66",
            measurementId: "G-NYRCMZMD71"
            };
    
        if (firebase.apps.length === 0) {
            firebase.initializeApp(firebaseConfig);
          }

          
    }


   // PEGANDO VALORES DO BANCO DE DADOS

    componentDidMount(){
    firebase.database().ref("Portao/estado").on('value', (snapshot)=> {
        let dado = snapshot.val() //poderia já setar o state aqui.)
            this.setState({
                      usuario: dado,
            }) 
        })

    firebase.database().ref("Abertura/valor").on('value', (snapshot)=> {
        let ValorAbertura = snapshot.val() //poderia já setar o state aqui.)
            this.setState({
                Abertura: ValorAbertura,
            }) 
        })
    }

render(){

    const { usuario, Abertura } = this.state; //usando a desestruturação
    
    return(
        <View>
            <Text>{usuario}</Text>
            <Text>{Abertura}</Text>
        </View>
            );
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    botao: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 5,
      width: 200,
      height: 200,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
    },
    botao: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 5,
      width: 200,
      height: 200,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
    },
    botaoMenor: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 3,
      width: 100,
      height: 100,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
      marginTop: 10,
      marginRight: 140,
    },
    botaoMenor2: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 3,
      width: 100,
      height: 100,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
      marginTop: -100,
      marginRight: -130,
    },
    imgLogo:{
      height: 160,
      width: 160,
      marginTop: 0,
      top: -60,
    },
    imgPedestre:{
      height: 80,
      width: 80,
      marginLeft: 10,
    },
    imgMoto:{
      height: 80,
      width: 80,
      marginLeft: 8,
      marginTop: 4,
    },
    imgPlay:{
      height: 200,
      width: 200,
    },
    estadoPortao: {
      fontSize: 40,
      fontFamily: 'Courier New, Courier, monospace',
      color: '#474954',
      marginLeft: -20
    },
    aberturaValor: {
      fontSize: 27,
      fontFamily: 'Courier New, Courier, monospace',
      color: '#fff',
      marginTop: -31,
      marginLeft: 135
    }
  });
