};

// INICIA O FIREBASE

if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}

function VerificarPortao(){
  firebase.database().ref('Portao/estado').on('value', querySnapShot => {
    let data = querySnapShot.val()

  firebase.database().ref('Abertura/valor').on('value', qSnapShot => {
      let dataVa = qSnapShot.val()
      dataVa = dataVa / 100
      Alert.alert(`Portão ${data} // ${dataVa}`)
      alert(`Portão ${data} //  ${dataVa} M`)
      return (
        <View><Text>Eae</Text></View>
      )
    });
  });
}


export default class App extends React.Component{

// DEFININDO AS FUNÇÕES DOS BOTÕES PRINCIPAIS

  acionar = () => {
    firebase.database().ref('controle/').set({
      estado: 'on',
    });
  }

  LongPress = () => {
    firebase.database().ref('LongPress/').set({
      estado: 'on',
    });
    VerificarPortao()
  }

  pedestre = () => {
    firebase.database().ref('pedestre/').set({
      estado: 'on',
    });
  }

  motocicletas = () => {
    firebase.database().ref('motocicletas/').set({
      estado: 'on',
    });
  }

  // LENDO OS DADOS DO ESP32







render(){

  return (
