      </TouchableOpacity>


      <TouchableOpacity style={styles.botaoMenor2}
      title="Motocicletas"
      onPress={this.motocicletas}
      >
        <Image source={require('./assets/moto.png')} style={styles.imgMoto}/>
      </TouchableOpacity>

    </View>
  )
}
  ;
}


