import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Image, Animated} from 'react-native';
import  firebase from 'firebase/app';
import 'firebase/database'
import 'firebase/firestore';
import 'firebase/auth';
import 'firebase/storage';
import Distancia from './src/components/Distancia';
//import database from '@react-native-firebase/database'
//import * as firebase from 'firebase';

let firebaseConfig = {
  apiKey: "AIzaSyA8LQOWe1hwNZzP3yxfYepZoHf4M-ds8D8",
  authDomain: "holdor-661d2.firebaseapp.com",
  databaseURL: "https://holdor-661d2-default-rtdb.firebaseio.com",
  projectId: "holdor-661d2",
  storageBucket: "holdor-661d2.appspot.com",
  messagingSenderId: "959425559187",
  appId: "1:959425559187:web:3d6444684619296a9b6b66",
  measurementId: "G-NYRMZMD71"
};

// INICIA O FIREBASE  export default 

if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}


export default class App extends Component{
//export default function App(){

  constructor(props) {
    super(props);
    this.state = {
        Portao: '',
        valor: '',
        };

    let firebaseConfig = {
        apiKey: "AIzaSyA8LQOWe1hwNZzP3yxfYepZoHf4M-ds8D8",
        authDomain: "holdor-661d2.firebaseapp.com",
        databaseURL: "https://holdor-661d2-default-rtdb.firebaseio.com",
        projectId: "holdor-661d2",
        storageBucket: "holdor-661d2.appspot.com",
        messagingSenderId: "959425559187",
        appId: "1:959425559187:web:3d6444684619296a9b6b66",
        measurementId: "G-NYRCMZMD71"
        };

    if (firebase.apps.length === 0) {
        firebase.initializeApp(firebaseConfig);
      }


}


   // PEGANDO VALORES DO BANCO DE DADOS

   componentDidMount(){
    firebase.database().ref("Portao/estado").on('value', (snapshot)=> {
        let Portao = snapshot.val() //poderia já setar o state aqui.)
            this.setState({
                      state: Portao,
            }) 
        })

    firebase.database().ref("Abertura/valor").on('value', (snapshot)=> {
        let ValorAbertura = snapshot.val() //poderia já setar o state aqui.)
        ValorAbertura = ValorAbertura / 100
            this.setState({
                Abertura: ValorAbertura,
            }) 
        })

    firebase.database().ref("UnidadeMedida").on('value', (snapshot)=> {
        let UnidadeSingular = snapshot.val() //poderia já setar o state aqui.)
            this.setState({
                singular: UnidadeSingular,
                plural: `${UnidadeSingular}s`
            }) 
        })
    }

// DEFININDO AS FUNÇÕES DOS BOTÕES PRINCIPAIS

  acionar = () => {
    firebase.database().ref('controle/').set({
      estado: 'on',
    });
  }

  LongPress = () => {
    firebase.database().ref('LongPress/').set({
      estado: 'on',
    });
    console.log('ok')
  }

  pedestre = () => {
    firebase.database().ref('pedestre/').set({
      estado: 'on',
    });
  }

  motocicletas = () => {
    firebase.database().ref('motocicletas/').set({
      estado: 'on',
    });
  }

render() {

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    sensor: {
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'red',
      zIndex: 10,
      top: -130,
    },
    info: {
      color: '#474954',
      fontSize: 35,
    },
    unidadeMedida: {
      fontSize: 15
    },
    graficoPortao: {
      width: 260,
      height: 35,
      borderColor: '#5adbff',
      borderWidth: 2,
      left: teste === teste ? teste : animaPortao,
      backgroundColor: '#474954',
      display: 'flex'
    },
    Botoes: {
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
      bottom: 25
    },
    botao: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 5,
      width: 200,
      height: 200,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
    },
    botao: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 5,
      width: 200,
      height: 200,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
    },
    botaoMenor: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 3,
      width: 100,
      height: 100,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
      marginTop: 10,
      marginRight: 140,
    },
    botaoMenor2: {
      backgroundColor: '#FFFFFF',
      borderColor: '#474954',
      borderWidth: 3,
      width: 100,
      height: 100,
      textAlign: 'center',
      fontSize: 20,
      borderRadius: 100,
      marginTop: -100,
      marginRight: -130,
    },
    imgLogo:{
      height: 140,
      width: 140,
      marginTop: 40,
      top: -160,
    },
    imgPedestre:{
      height: 80,
      width: 80,
      marginLeft: 10,
    },
    imgMoto:{
      height: 80,
      width: 80,
      marginLeft: 8,
      marginTop: 4,
    },
    imgPlay:{
      height: 200,
      width: 200,
    },
    estadoPortao: {
      fontSize: 40,
      fontFamily: 'Courier New, Courier, monospace',
      color: '#474954',
      marginLeft: -20
    },
    aberturaValor: {
      fontSize: 27,
      fontFamily: 'Courier New, Courier, monospace',
      color: '#fff',
      marginTop: -31,
      marginLeft: 135
    }
  });

  const { state, Abertura, singular, plural } = this.state; //usando a desestruturação
  let animaPortao = Abertura * 60
  let UnMedida = Abertura < 1.01 ? singular : plural
  let teste = animaPortao

  return (
    <View style={styles.container}>


<Image source={require('./assets/gcarfelogo.png')} style={styles.imgLogo}/>

      <StatusBar style="auto" />


    <View style={styles.sensor}>
      <Text style={styles.info}>{state}</Text>
      <View style={styles.graficoPortao}></View>
      <Text style={styles.info}>{Abertura} <Text style={styles.unidadeMedida}>{UnMedida}</Text></Text>
    </View>



    <View style={styles.Botoes}>
      <TouchableOpacity style={styles.botao}
      title="Acionar"
      onPress={this.acionar}
      onLongPress={this.LongPress}>

        <Image source={require('./assets/playButton.png')} style={styles.imgPlay}/>
      </TouchableOpacity>


      <TouchableOpacity style={styles.botaoMenor}
      title="Pedestres"
      onPress={this.pedestre}
      >
         <Image source={require('./assets/pedestre.png')} style={styles.imgPedestre}/>
      </TouchableOpacity>


      <TouchableOpacity style={styles.botaoMenor2}
      title="Motocicletas"
      onPress={this.motocicletas}
      >
        <Image source={require('./assets/moto.png')} style={styles.imgMoto}/>
      </TouchableOpacity>
    </View>

    </View>
  )
};
}
