import React, {Component} from 'react';
import { Stylesheet, Text, View, TextInput, Button, ColorPropType} from 'react-native';
import  firebase from 'firebase/app';
import 'firebase/database'
import 'firebase/'
import 'firebase/auth';
import 'firebase/storage';


//firebase.initializeApp(firebaseConfig);
  
export default class Sensor extends Component{

    constructor(props) {
        super(props);
        this.state = {
            Portao: '',
            valor: '',
            };
          
        let firebaseConfig = {
            apiKey: "AIzaSyA8LQOWe1hwNZzP3yxfYepZoHf4M-ds8D8",
            authDomain: "holdor-661d2.firebaseapp.com",
            databaseURL: "https://holdor-661d2-default-rtdb.firebaseio.com",
            projectId: "holdor-661d2",
            storageBucket: "holdor-661d2.appspot.com",
            messagingSenderId: "959425559187",
            appId: "1:959425559187:web:3d6444684619296a9b6b66",
            measurementId: "G-NYRCMZMD71"
            };
    
        if (firebase.apps.length === 0) {
            firebase.initializeApp(firebaseConfig);
          }

          
    }


   // PEGANDO VALORES DO BANCO DE DADOS

    componentDidMount(){
    firebase.database().ref("Portao/estado").on('value', (snapshot)=> {
        let dado = snapshot.val() //poderia já setar o state aqui.)
            this.setState({
                      usuario: dado,
            }) 
        })

    firebase.database().ref("Abertura/valor").on('value', (snapshot)=> {
        let ValorAbertura = snapshot.val() //poderia já setar o state aqui.)
            this.setState({
                Abertura: ValorAbertura,
            }) 
        })
    }

render(){

    const { usuario, Abertura } = this.state; //usando a desestruturação
    
    return(
        <View>
            <Text
            style={
                {
                    color: '#474954',
                    fontSize: 35,
                    flex: 1,
                    alignItems: 'center',
                    justifyContent: 'center',
                }
            }
            >
                {usuario}
            </Text>

            <Text
                style={
                    {
                        color: '#474954',
                        fontSize: 35,
                        paddingLeft: 28
                    }
                }
            >
                {Abertura}
            </Text>
        </View>
            );
    }
}
