import React, {Component} from 'react';
import { Stylesheet, Text, View, TextInput, Button} from 'react-native';
import  firebase from 'firebase/app';
import 'firebase/database'
import 'firebase/'
import 'firebase/auth';
import 'firebase/storage';



  
//firebase.initializeApp(firebaseConfig);
  
export default class TelaLogin extends Component{

    constructor(props) {
        super(props);
        this.state = {
            cidadeINput:'',
            emailInput:'',
            estadoInput:'',
            nomeINput:'',
            senhaInput:'',
            usuarioInput:'',
            };
          
        let firebaseConfig = {
            apiKey: "AIzaSyA8LQOWe1hwNZzP3yxfYepZoHf4M-ds8D8",
            authDomain: "holdor-661d2.firebaseapp.com",
            databaseURL: "https://holdor-661d2-default-rtdb.firebaseio.com",
            projectId: "holdor-661d2",
            storageBucket: "holdor-661d2.appspot.com",
            messagingSenderId: "959425559187",
            appId: "1:959425559187:web:3d6444684619296a9b6b66",
            measurementId: "G-NYRCMZMD71"
            };
    
        if (firebase.apps.length === 0) {
            firebase.initializeApp(firebaseConfig);
          }

          
    }


          // Notei que você recebe só o nome na função, poderia retornar todos os dados tbm se necessário
    componentDidMount(){
    let data;
    firebase.database().ref("Portao/estado").on('value', (snapshot)=> data = snapshot.val()); //poderia já setar o state aqui.)
    this.setState({
                      usuario: data
              }) 
          }

    render(){


    const { usuario, senha } = this.state; //usando a desestruturação
    
    return(
            <View>
          
                <TextInput value={usuario} placeholder="Usuário" onChangeText={(usuarioInput)=>this.setState({usuarioInput})} />
          
                <TextInput placeholder="Senha" 
                value={senha} onChangeText={(senhaInput)=>this.setState({senhaInput})} />
          
                <Button title="inserir usuário" onPress={this.inserirUsuario} />
            </View>
                      );
                  }
          }
